<?phpnamespace App\Http\Controllers;use App\Mail\SendingMailToCustomer;use Illuminate\Http\Request;use Illuminate\Support\Facades\Mail;use App\Mail\SendingMailToMe;use Illuminate\Support\Facades\Validator;class MailController extends Controller{    public function sendMail(Request $request){        $customMessages = [            'name.required' => 'Mời Quý Khách Nhập Tên Liên Hệ',            'email.required' => 'Mời Quý Khách Nhập Email',            'email.email' => 'Mời Quý Khách Nhập Nhập Đúng Định Dạng Email',            'phone.required' => 'Xin Vui Lòng Nhập Số Điện Thoại',        ];        $this->validate($request, [            'name' => 'required',            'email'=>'required|email',            'phone'=>'required'        ],$customMessages);        Mail::send(new SendingMailToMe());        Mail::send(new SendingMailToCustomer());        return response()->json([            'success' => true        ]);    }    public function sendPhone(Request $request){        $validator = Validator::make($request->all(), [            'phone' => 'required',        ],[            'phone.required' => 'Mời Quý Khách Nhập Tên Liên Hệ',        ]);        if ($validator->fails()) {            return response()->json([                'success' => false,                'validator'=>$validator->errors()            ]);        }        Mail::send(new SendingPhoneToMe());        return response()->json([            'success' => true        ]);    }}